mapper.select:

where d.del_flag = '0'

  AND parent_id = #{parentId}


  AND dept_name like concat('%', #{deptName}, '%')


  AND status = #{status}


${params.dataScope}
order by d.parent_id, d.order_num

mapper.update:
update sys_dept set ancestors =

  when #{item.deptId} then #{item.ancestors}

where dept_id in

  #{item.deptId}

mapper.select:
select count(1) from sys_dept
where del_flag = '0'
 and dept_id = #{deptId} 
 and parent_id = #{parentId}
